Bash(powershell -Command "Add-Type @''
using System;
using System.Runtime.InteropServices;
public class Win32 {
    [DllImport(""user32.dll"")]
    public static extern bool SetForegroundWindow(IntPtr hWnd);
    [DllImport(""user32.dll"")]
    public static extern bool ShowWindow(IntPtr hWnd, int nCmdShow);
}
''@; Get-Process electron -ErrorAction SilentlyContinue | ForEach-Object { if ($_.MainWindowHandle -ne 0) { [Win32]::ShowWindow($_.MainWindowHandle, 5); [Win32]::SetForegroundWindow($_.MainWindowHandle) } }")
